import React, { useState } from 'react';
import fs from 'fs';
import { IonAlert, IonButton, IonCard, IonCardContent, IonCardHeader, IonCardSubtitle, IonCardTitle, IonContent, IonHeader, IonImg, IonPage, IonTitle, IonToolbar } from '@ionic/react';
import ExploreContainer from '../components/ExploreContainer';
import {shareAlbum} from '../FirebaseConfig'
import './Search.css';
import { toast } from '../Toast';
import albums from '../assets/json/album.json';

const Search: React.FC = () => {

  const JsonAlbums = albums
  console.log(JsonAlbums)
  const [busy, setBusy] = useState<boolean>(false)

  async function share(value: string) {
      setBusy(true)
      const res = await shareAlbum(value)
      toast('Album Shared')
      setBusy(false)
  }

  const [showAlert, setShowAlert] = useState(false);


  function addAlbum(data: { title: string; artist: string; date_sortie: string; length: string; nbr_song: string; cover: string; spotify: string; }){
    var table = []
    table.push({title: data.title, artist:data.artist, data_sortie:data.date_sortie, 
                    length: data.length, nbr_song: data.nbr_song, cover: data.cover, spotify: data.spotify});
    console.log(table);
    var json = JSON.stringify(table);
    fs.writeFile('myjsonfile.json', json, 'utf8',function(err) {
      if(err) {
          return console.log(err);
      }

      console.log("The file was saved!");
  }); 
  }

  return (



    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Search</IonTitle>
          <IonButton onClick={() => setShowAlert(true)} expand="block">Show Alert 1</IonButton>
        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen>
      <IonAlert
          isOpen={showAlert}
          onDidDismiss={() => setShowAlert(false)}
          cssClass='my-custom-class'
          header={'Add Album'}
          inputs={[
            {
              name: 'title',
              type: 'text',
              placeholder: 'Title'
            },
            {
              name: 'artist',
              type: 'text',
              placeholder: 'Artist'
            },
            {
              name: 'date_sortie',
              type: 'text',
              placeholder: 'Date'
            },
            // input date with min & max
            {
              name: 'length',
              type: 'text',
              placeholder: 'Length'
            },
            // input date without min nor max
            {
              name: 'nbr_song',
              type: 'text',
              placeholder: 'Number Of Songs'
            },
            {
              name: 'cover',
              type: 'text',
              placeholder: 'Cover'
            },
            {
              name: 'spotify',
              type: 'text',
              placeholder: "Spotify Link"
            },
          ]}
          buttons={[
            {
              text: 'Cancel',
              role: 'cancel',
              cssClass: 'secondary',
              handler: () => {
                console.log('Confirm Cancel');
              }
            },
            {
              text: 'Ok',
              handler: data => {
                console.log('Confirm Ok');
                addAlbum(data);
              }
            }
          ]}
        />
      {JsonAlbums.album.map((alb,i) => (
      <><IonCard>
      <IonImg src={alb.cover}/>
          <IonCardHeader>
            <IonCardSubtitle>{alb.title}</IonCardSubtitle>
            <IonCardTitle>{alb.artist}</IonCardTitle>
          </IonCardHeader>

          <IonCardContent>
            {alb.length}{alb.nbr_song}{alb.date_sortie}
            <p><iframe src={alb.spotify} width="300" height="380" allow="encrypted-media"></iframe></p>
      </IonCardContent>
      <IonButton onClick={() => share('{alb.title}')}> Partager </IonButton>
        </IonCard> </>))}

      </IonContent>
    </IonPage>
  );
};

export default Search;
