import React, { useState } from 'react';
import { IonCard, IonCardContent, IonCardHeader, IonCardSubtitle, IonCardTitle, IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonImg } from '@ionic/react';
import ExploreContainer from '../components/ExploreContainer';
import './Fil.css';
import AddItem from '../AddItem';
import ItemList from '../ItemList';
import { toast } from '../Toast';
import posts from '../assets/json/posts.json';
import { cpuUsage } from 'process';
import { getEmail } from '../FirebaseConfig';

const [busy, setBusy] = useState<boolean>(false)
const email = getEmail();
var username: string 
if (email == 'leo.hamon@h.com'){username = 'Leo_Le_Hamon'}
if (email == 's.k@skg.fr'){username = 'SKG'}
if (email == 'harden@mail.fr'){username = 'LebronGOAT'}
var JsonPosts = posts;

export function share(title: string,artist: string,date_sortie: string, length: string, nbr_song: string,
  cover: string, spotify: string){
  let date = Date();
  let dateLoc = date.toLocaleString()
  JsonPosts["posts"].push({username: username, date: dateLoc, album: [
        {   
            title: title,
            artist: artist,
            date_sortie: date_sortie,
            length: length,
            nbr_song: nbr_song,
            cover: cover,
            spotify: spotify
        }
    ]
})
  setBusy(true)
  
  toast('Album Shared')
  setBusy(false)
}

const Fil: React.FC = () => {

  return (
    <IonPage>
      <IonContent fullscreen>
        <IonHeader collapse="condense">
          <IonToolbar>
            <IonTitle size="large" id="titreInsta">InstaSound</IonTitle>
          </IonToolbar>
        </IonHeader>
        {JsonPosts.posts.map((post,i) => (
      <><IonCard>
          <IonCardHeader>
            <IonCardSubtitle>{post.username}</IonCardSubtitle>
          </IonCardHeader>
          <IonCardContent>
          {post.album.map((alb,i) => (
              <><div>{alb.title}</div>
              <IonImg src={alb.cover} alt="dfdfd" /></>
            ))}
          {post.comments.map((comment,i) => (
            <><p>{comment.date} {comment.heure} </p><p>{comment.username}: {comment.message}</p></>
          ))}
            </IonCardContent>
        </IonCard> </>))}
      </IonContent>
    </IonPage>
  );
};

export default Fil;
